  @ViewChild('code') code: ElementRef;
  faCopy = faCopy;
  copied = false;
  copiedTimeout: any;

  /**
   * @ignore
   */
  ngOnDestroy() {
    clearTimeout(this.copiedTimeout);
  }

  /**
   * @ignore
   */
  copy() {
    this.copyToClipboard(this.code.nativeElement.innerText);
    this.copied = true;
    clearTimeout(this.copiedTimeout);
    this.copiedTimeout = setTimeout(() => this.copied = false, 3000);
  }

